// Constructor
CIDHashContainerDelayedSorting::CIDHashContainerDelayedSorting()
{
	this->bIsArraySorted = true;
}

// Allocate element
void CIDHashContainerDelayedSorting::AllocateElement(int32_t ID, uint32_t hash)
{
	tIDhashPair pair;
	pair.ID = ID;
	pair.hash = hash;
	this->array.push_back(pair);

	this->bIsArraySorted = false;

	// CGenericLogStorage::SaveFormattedTextLn("Adding ID: %d", pair.ID);
	// CGenericLogStorage::SaveWorkBuffer();

	//if (ID == 344)
		//printf_MessageBox("this");
}

// Updates element
void CIDHashContainerDelayedSorting::UpdateElement(int32_t ID, uint32_t oldHash, uint32_t newHash)
{
	auto it = this->GetElementItByID(ID);

	if (oldHash == it->hash)
		return;

	this->array.erase(it);

	tIDhashPair pair;
	pair.ID = ID;
	pair.hash = newHash;
	this->array.push_back(pair);
	this->bIsArraySorted = false;


	// CGenericLogStorage::SaveFormattedTextLn("Updating ID: %d", pair.ID);
	// CGenericLogStorage::SaveWorkBuffer();
}

bool HashPairsCompareHash(const tIDhashPair& a, const tIDhashPair& b)
{
	return a.hash < b.hash;
}

// Returns ID by hash
int32_t CIDHashContainerDelayedSorting::GetIDbyHashBinarySearch(uint32_t hash)
{
	// Sort the array if neccessary
	this->SortTheArray();

	// Keep halving the search space until we reach the end of the vector
	auto begin = this->array.begin();
	auto end = this->array.end();

	while (begin < end)
	{
		// Find the median value between the iterators
		const auto Middle = begin + (std::distance(begin, end) / 2);

		// Re-adjust the iterators based on the median value
		if (Middle->hash == hash)
			return Middle->ID;
		else if (Middle->hash > hash)
			end = Middle;
		else
			begin = Middle + 1;
	}

	return UNDEFINED_FILE_ID_INT32;
}

// Returns ID by hash
int32_t CIDHashContainerDelayedSorting::GetIDbyHashLinearSearch(uint32_t hash)
{
	for (auto it = this->array.begin(); it != this->array.end(); ++it)
		if (it->hash == hash)
			return it->ID;

	return UNDEFINED_FILE_ID_INT32;
}

// Sorts the array
void CIDHashContainerDelayedSorting::SortTheArray()
{
	if (!this->bIsArraySorted)
	{
		std::sort(this->array.begin(), this->array.end(), &HashPairsCompareHash);	
		// CGenericLogStorage::SaveTextLn("Sorting the array!");
		// CGenericLogStorage::SaveWorkBuffer();
		this->bIsArraySorted = true;
	}
}

// Clears the array
void CIDHashContainerDelayedSorting::Clear()
{
	this->array.clear();
}

// Returns element iterator by ID
CIDHashContainerDelayedSorting::tEntryContainer::iterator CIDHashContainerDelayedSorting::GetElementItByID(int32_t ID)
{
	for (auto it = this->array.begin(); it != this->array.end(); ++it)
		if (it->ID == ID)
			return it;

	return this->array.end();
}

///////////

// Allocate element
void CIDHashContainerAlwaysSorted::AllocateElement(int32_t ID, uint32_t hash)
{
	this->array[hash] = ID;
}

// Updates element
void CIDHashContainerAlwaysSorted::UpdateElement(int32_t ID, uint32_t oldHash, uint32_t newHash)
{
	this->array[oldHash] = newHash;
}

// Updates element
void CIDHashContainerAlwaysSorted::RemoveElement(int32_t ID, uint32_t oldHash)
{
	auto oldHashIt = this->array.find(oldHash);

	if (oldHashIt != this->array.end() && oldHashIt->second == ID)
		this->array.erase(oldHashIt);
	else
	{
		for (auto it = this->array.begin(); it != this->array.end(); ++it)
			if (it->second == ID)
			{
				this->array.erase(it);
				return;
			}
	}
}

// Returns ID by hash
int32_t CIDHashContainerAlwaysSorted::GetIDbyHashBinarySearch(uint32_t hash)
{
	auto it = this->array.find(hash);

	return it != this->array.end() ? it->second : UNDEFINED_FILE_ID_INT32;

}

// Clears the array
void CIDHashContainerAlwaysSorted::Clear()
{
	this->array.clear();
}

#define MAKE_FILE_TYPE_PAIR(member, generalFileType) { member, #member, generalFileType },

const tFileTypeDescriptor CFileTypeInfo::fileDescriptorArray[] =
{
	MAKE_FILE_TYPE_LIST(MAKE_FILE_TYPE_PAIR)
};

/* Finds enum member by name */
eFileType CFileTypeInfo::GetEnumMemberByName(const char* name)
{
	for (unsigned int i = 0; i != _countof(fileDescriptorArray); i++)
		if (!strcmp(name, fileDescriptorArray[i].fileTypeName))
			return fileDescriptorArray[i].fileType;

	return FILE_TYPE_UNDEFINED;
}

/* Finds name by enum member */
const char* CFileTypeInfo::GetEnumNameByMember(eFileType member)
{
	const tFileTypeDescriptor* ptr = GetFileInfoDescriptorByMember(member);

	return ptr ? ptr->fileTypeName : NULL;
}

/* Finds general file type descriptor by enum member */
const tFileTypeDescriptor* CFileTypeInfo::GetFileInfoDescriptorByMember(eFileType member)
{
	for (unsigned int i = 0; i != _countof(fileDescriptorArray); i++)
		if (fileDescriptorArray[i].fileType == member)
			return &fileDescriptorArray[i];

	return NULL;
}

// Returns array index
int FileIDlimit::GetFileTypeArrayIndex(eFileType type)
{
	for (int i = 0; i < countOfTypes; i++)
		if (fileTypeArray[i].fileType == type)
			return i;

	return -1;
}

// Returns pointer to file type info
const tFileTypeInfo* details::FileIDlimit_base1::GetFileTypeInfo(eFileType type) const
{
	for (int i = 0; i < countOfTypes; i++)
		if (fileTypeArray[i].fileType == type)
			return &fileTypeArray[i];

	return nullptr;
}

// Returns pointer to file type info
const tFileTypeInfo* details::FileIDlimit_base1::GetFileTypeInfo(eFileTypeGeneral type) const
{
	for (int i = 0; i < countOfTypes; i++)
		if (fileTypeArray[i].pFileTypeDescriptor->generalFileType == type)
			return &fileTypeArray[i];

	return nullptr;
}

// Registers file type
void FileIDlimit::RegisterFileType(eFileType fileType, int defaultCount, tFileTypeLimit maxLimitValue)
{
	int index = this->countOfTypes++;

	tFileTypeInfo* pTypeInfo = &this->fileTypeArray[index];
	
	pTypeInfo->maxLimitValue = maxLimitValue;
	pTypeInfo->fileType = fileType;
	pTypeInfo->pFileTypeDescriptor = CFileTypeInfo::GetFileInfoDescriptorByMember(fileType);

	pTypeInfo->currentBaseID = 0;
	pTypeInfo->defaultBaseID = 0;
	pTypeInfo->defaultLimitValue = defaultCount;
	pTypeInfo->currentLimitValue = defaultCount;
}

// Returns maximum number of file IDs
uint32_t FileIDlimit::GetMaxNumberOfFileIDs() const
{
	return this->maxCountOfIDs;
}

// Returns true, if there is any ID limit that can be modified
bool FileIDlimit::IsThereAnyIDlimitThatCanBeModified() const
{
	for (int i = 0; i < countOfTypes; i++)
		if (fileTypeArray[i].maxLimitValue > 0)
			return true;

	return false;
}

// Updates base IDs, needs to be executed 
void FileIDlimit::UpdateBaseIDs()
{
	int curBaseID = 0;
	int defBaseID = 0;

	this->fileTypeArray[0].currentBaseID = 0;
	this->fileTypeArray[0].defaultBaseID = 0;

	for (int i = 0; i < countOfTypes; ++i)
	{
		tFileTypeBaseID currentLimitValue = this->fileTypeArray[i].currentLimitValue;
		tFileTypeBaseID defaultLimitValue = this->fileTypeArray[i].defaultLimitValue;

		this->fileTypeArray[i + 1].currentBaseID = curBaseID + currentLimitValue;
		this->fileTypeArray[i + 1].defaultBaseID = defBaseID + defaultLimitValue;

		curBaseID += currentLimitValue;
		defBaseID += defaultLimitValue;
	};
}

// Enables/disables ID limits patch.
void FileIDlimit::EnableIDlimitsPatch(bool bEnabled)
{
	if (this->countOfTypes > 0)
		bIDlimitsPatchEnabled = bEnabled;
}

// Returns true if ID limits are known
bool FileIDlimit::AreIDlimitsKnown() const
{
	return this->GetCountOfTypes() > 0;
}

// Returns true if ID limits patch is enabled
bool FileIDlimit::IsIDlimitsPatchEnabled() const
{
	return this->bIDlimitsPatchEnabled;
}

// Writes to log if limits are changed
bool FileIDlimit::WriteToLogIfLimitsAreChanged(bool bModelSpecialFeaturesImplemented)
{
	// if debug, set ID limits to default values 
	if (!CPatch::IsDebugModeActive())
	{
		if (IsAnyIDlimitChanged())
			CGenericLogStorage::SaveTextLn("At least one ID limit is changed. ID limits will be patched.");
		else if(bModelSpecialFeaturesImplemented && g_modelSpecialFeatures.AreSpecialFeaturesEnabled())
			CGenericLogStorage::SaveTextLn("Model special features are enabled. Because of this, the ID limits will be patched.");
		else
		{
			CGenericLogStorage::SaveTextLn("None of ID limits is changed. Nothing to do.");
			CGenericLogStorage::WriteLineSeparator();
			return false;
		}
	}
	else
	{
		SetIDlimitsToDefaultValues();
		CGenericLogStorage::SaveTextLn("ID limits working in debug mode.");
	}

	return true;
}

// Returns true if any ID limit is increased.
bool FileIDlimit::IsAnyIDlimitIncreased() const
{
	for (int i = 0; i < countOfTypes; ++i)
	{
		const tFileTypeInfo* pFileInfo = &this->fileTypeArray[i];

		if (pFileInfo->currentLimitValue > pFileInfo->defaultLimitValue)
			return true;
	}

	return false;
}

// Returns true if any ID limit is changed.
bool FileIDlimit::IsAnyIDlimitChanged() const
{
	for (int i = 0; i < countOfTypes; ++i)
	{
		const tFileTypeInfo* pFileInfo = &this->fileTypeArray[i];

		if (pFileInfo->currentLimitValue != pFileInfo->defaultLimitValue)
			return true;
	}

	return false;
}

// Sets ID limits to default values.
void FileIDlimit::SetIDlimitsToDefaultValues()
{
	for (int i = 0; i <= countOfTypes; ++i)
	{
		tFileTypeInfo* pFileInfo = &this->fileTypeArray[i];
		
		pFileInfo->currentBaseID = pFileInfo->defaultBaseID;
		pFileInfo->currentLimitValue = pFileInfo->defaultLimitValue;
	}
}

// Returns file type info by index
tFileTypeInfo* FileIDlimit::GetFileTypeInfoByIndex(int index)
{
	return &this->fileTypeArray[index];
}

