	// Initializes CStreamingInfo
	void CStreamingInfo::Init()
	{
		this->uiLoadFlag = 0;
		this->usNext = 0;
		this->usPrev = 0;
		this->usNextOnCd = -1;
		this->_unk_inCdImage = 0;
		this->FilePositionInBlocks = 0;
	}

	void __cdecl patch_CStreaming__Init_doIt()
	{
		tFileTypeBaseID count = g_fileIDlimits.GetCountOfAllFileIDs();

		for (tFileTypeBaseID ID = 0; ID < count; ID++)
			g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_vc[ID].Init();
	}

