		// Returns pointer to extended value
		void* realGetPointerToExtendedValue(uint32_t InfoForModelOffset, uint32_t memberOffset)
		{		
			#ifdef ENABLE_FILE_INFO_EXTENSION
			uint32_t fileID = InfoForModelOffset / sizeof(CStreamingInfo);

			CStreamingInfoExtension* pExtension = &g_fileIDlimits.CStreaming__ms_aInfoForModelExtension[fileID];

			switch (memberOffset)
			{
				case 0:
					return &pExtension->exUsNext;
				case 2:
					return &pExtension->exUsPrev;
				case 4:
					return &pExtension->exNextOnCd;
			}
			#else
			CStreamingInfo* pFileInfo = (CStreamingInfo*)((char*)(g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa) + InfoForModelOffset);

			switch (memberOffset)
			{
			case 0:
				return &pFileInfo->usNext;
			case 2:
				return &pFileInfo->usPrev;
			case 4:
				return &pFileInfo->usNextOnCd;
			}
			#endif
			return NULL;
		}

		int32_t __cdecl realGetExtendedMemberByStreamingInfoMemOffset(uint32_t InfoForModelOffset, uint32_t memberOffset)
		{
#ifdef ENABLE_FILE_INFO_EXTENSION
			return *(int32_t*)realGetPointerToExtendedValue(InfoForModelOffset, memberOffset);
#else
			// DWORD result =  *((uint16_t*)((char*)FileIDlimit::CStreaming__ms_aInfoForModel + InfoForModelOffset + memberOffset));

			uint32_t result = *(uint16_t*)realGetPointerToExtendedValue(InfoForModelOffset, memberOffset);

			if (result == 0xFFFF)
				result = UNDEFINED_FILE_ID_INT32;

			return result;
#endif		
			

		}

		void __cdecl realSetExtendedMemberByStreamingInfoMemOffset(uint32_t InfoForModelOffset, uint32_t memberOffset, uint32_t newValue)
		{
#ifdef ENABLE_FILE_INFO_EXTENSION
			*(int32_t*)realGetPointerToExtendedValue(InfoForModelOffset, memberOffset) = newValue;
#else
			// *((uint16_t*)((char*)FileIDlimit::CStreaming__ms_aInfoForModel + InfoForModelOffset + memberOffset)) = (uint16_t)newValue;
			*(uint16_t*)realGetPointerToExtendedValue(InfoForModelOffset, memberOffset) = newValue;
#endif

			
		}

