	/*
		enhanced format of model flags
			version 1 - uses 16-bit counts of files
			version 2 - uses 32-bit counts of files
	*/

	// Patch for readModelFlagsFromSave
	bool __cdecl CStreaming__Load()
	{
		char buffer[ENHANCED_FLAG_IDENTIFIER_LEN + 1];

		for(unsigned int i = 0; i < ENHANCED_FLAG_IDENTIFIER_LEN; i++)
			CGenericGameStorage::LoadDataFromWorkBuffer(buffer + i, 1);
		
		if (strncmp(buffer, ENHANCED_FLAG_IDENTIFIER, ENHANCED_FLAG_IDENTIFIER_LEN))	// is standard version?
		{
			tFileTypeBaseID defaultCount = g_fileIDlimits.GetDefaultCountOfAllFileIDs();

			uint8_t* pFlags = new uint8_t[defaultCount];

			memcpy(pFlags, buffer, ENHANCED_FLAG_IDENTIFIER_LEN);

			// read the rest of data
			for (unsigned int i = 0; i < defaultCount - ENHANCED_FLAG_IDENTIFIER_LEN; i++)
				CGenericGameStorage::LoadDataFromWorkBuffer(pFlags + ENHANCED_FLAG_IDENTIFIER_LEN + i, 1);
			
			for (eFileType type : arrayOfFileTypes)
			{
				uint16_t numberOfFiles = g_fileIDlimits.GetFileIDdefaultLimit(type);

				if (g_fileIDlimits.GetFileIDcurrentLimit(type) < numberOfFiles)
					numberOfFiles = g_fileIDlimits.GetFileIDcurrentLimit(type);

				uint8_t* iFlags = pFlags + g_fileIDlimits.GetDefaultBaseID(type);
				CStreamingInfo* oInfoForModel = &g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa
					[g_fileIDlimits.GetBaseID(type)];
				
				for (uint16_t i = 0; i < numberOfFiles; i++)
				{
					CStreamingInfo* file = oInfoForModel + i;

					if (file->uiLoadFlag == 1 && iFlags[i] != 0xFF)
						file->ucFlags |= iFlags[i];
				}

				/*
				printf_MessageBox("File type: %d Number of source IDs: %d Input ID: %d Output ID: %d",
				type,
				numberOfFiles,
				CLimitAdjusterMain::CStreaming__defaultBaseID[type],
				CLimitAdjusterMain::GetBaseID(type)
				);
				*/
			}

			delete[] pFlags;
		}
		else
		{
			char format;

			// enhanced version
			CGenericGameStorage::LoadDataFromWorkBuffer(&format, 1);

			if (format == 1 || format == 2)
			{
				uint32_t numberOfBytesFollowing;
				CGenericGameStorage::LoadDataFromWorkBuffer(&numberOfBytesFollowing, sizeof(numberOfBytesFollowing));

				for (eFileType type : arrayOfFileTypes)
				{
					uint32_t numberOfFilesToProcess = 0;
					uint16_t numberOfFilesToSkip = 0;
					
					if(format == 1)
						CGenericGameStorage::LoadDataFromWorkBuffer(&numberOfFilesToProcess, sizeof(uint16_t));
					else if(format == 2)
						CGenericGameStorage::LoadDataFromWorkBuffer(&numberOfFilesToProcess, sizeof(uint32_t));

					uint16_t numberOfFilesInGame = g_fileIDlimits.GetFileIDcurrentLimit(type);

					CStreamingInfo* oInfoForModel = &g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa
						[g_fileIDlimits.GetBaseID(type)];

					if (numberOfFilesInGame < numberOfFilesToProcess)
					{
						numberOfFilesToSkip = numberOfFilesToProcess - numberOfFilesInGame;
						numberOfFilesToProcess = numberOfFilesInGame;
					}

					/*
					printf_MessageBox("File type: %d\nNumber of files to process: %d\nNumber of files to skip: %d\nOutput ID: %d",
					type,
					numberOfFilesToProcess,
					numberOfFilesToSkip,
					CLimitAdjusterMain::GetBaseID(type)
					);
					*/

					uint8_t flags;

					// Process file flags
					for (uint32_t i = 0; i < numberOfFilesToProcess; i++)
					{
						CGenericGameStorage::LoadDataFromWorkBuffer(&flags, 1);

						CStreamingInfo* file = oInfoForModel + i;

						if (file->uiLoadFlag == 1 && flags != 0xFF)
							file->ucFlags |= flags;
					}

					// Skip file flags
					while (numberOfFilesToSkip-- > 0)
						CGenericGameStorage::LoadDataFromWorkBuffer(&flags, 1);
				}
			}
		}
		
		return true;
	}

	bool __cdecl saveModelFlagsInSave_patch_standard()
	{
		for (eFileType type : arrayOfFileTypes)
		{
			tFileTypeBaseID defaultNumberOfFiles = g_fileIDlimits.GetFileIDdefaultLimit(type);

			tFileTypeBaseID numberOfFilesToProcess = g_fileIDlimits.GetFileIDcurrentLimit(type);
			tFileTypeBaseID numberOfFilesToSkip = defaultNumberOfFiles - numberOfFilesToProcess;

			CStreamingInfo* iInfoForModel = &g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa
				[g_fileIDlimits.GetBaseID(type)];

			/*
			printf_MessageBox("File type: %d\nNumber of files to process: %d\nNumber of files to skip: %d\nInput ID: %d Output ID: %d",
			type,
			numberOfFilesToProcess,
			numberOfFilesToSkip,
			CLimitAdjusterMain::GetBaseID(type),
			CLimitAdjusterMain::CStreaming__defaultBaseID[type]
			);
			*/

			uint8_t flags;

			for (uint16_t i = 0; i < numberOfFilesToProcess; i++)
			{
				CStreamingInfo* file = iInfoForModel + i;

				flags = -1;

				if (file->uiLoadFlag == 1)
					flags = file->ucFlags;

				CGenericGameStorage::SaveDataToWorkBuffer(&flags, 1);
			}

			// Skip files
			flags = -1;

			while (numberOfFilesToSkip-- > 0)
				CGenericGameStorage::SaveDataToWorkBuffer(&flags, 1);
		}

		return true;
	}

	bool __cdecl saveModelFlagsInSave_patch_enhanced()
	{
		for (unsigned int i = 0; i < ENHANCED_FLAG_IDENTIFIER_LEN; i++)
			CGenericGameStorage::SaveDataToWorkBuffer(ENHANCED_FLAG_IDENTIFIER + i, 1);

		char format = 1;
		CGenericGameStorage::SaveDataToWorkBuffer(&format, 1);
		
		uint32_t numberOfBytesFollowing = _countof(arrayOfFileTypes) * sizeof(uint16_t);

		for (eFileType type : arrayOfFileTypes)
			numberOfBytesFollowing += g_fileIDlimits.GetFileIDcurrentLimit(type);		
		
		CGenericGameStorage::SaveDataToWorkBuffer(&numberOfBytesFollowing, sizeof(numberOfBytesFollowing));

		for (eFileType type : arrayOfFileTypes)
		{
			uint16_t numberOfFiles = g_fileIDlimits.GetFileIDcurrentLimit(type);

			CGenericGameStorage::SaveDataToWorkBuffer(&numberOfFiles, sizeof(numberOfFiles));

			CStreamingInfo* FileInfoBase = &g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa
				[g_fileIDlimits.GetBaseID(type)];

			for (uint16_t i = 0; i < numberOfFiles; i++)
			{
				CStreamingInfo* file = FileInfoBase + i;

				uint8_t flags = -1;

				if (file->uiLoadFlag == 1)
					flags = file->ucFlags;

				CGenericGameStorage::SaveDataToWorkBuffer(&flags, 1);
			}
		}

		return true;
	}

	static void ApplyPatchForCStreaming__Save(FileIDlimit* pFileIDlimits, uintptr_t functionJumpAddress)
	{
		if (pFileIDlimits->IsAnyIDlimitIncreased())
		{
			CPatch::RedirectFunction(functionJumpAddress, (void*)&saveModelFlagsInSave_patch_enhanced);

			CGenericLogStorage::SaveText("Format of new savefiles will be different: patch for enhanced format of model flags block is applied!");
			g_SaveOfVariableLength.MakeSaveOfVariableLength();
		}
		else
			CPatch::RedirectFunction(functionJumpAddress, (void*)&saveModelFlagsInSave_patch_standard);
	}

	// Initializes prev, next for info for model.
	void InitPrevNextForInfoForModel(tFileTypeBaseID firstID, tFileTypeBaseID lastID, CStreamingInfo** ppStart, CStreamingInfo** ppEnd)
	{
		*ppStart = &g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa[firstID];
		*ppEnd = &g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa[lastID];

		tFileTypeBaseID curID = firstID;

		if (g_fileIDlimits.bIsIDbaseInt32)
		{
			do
			{
				if (curID == firstID)
					g_fileIDlimits.CStreaming__ms_aInfoForModelExtension[curID].exUsPrev = -1;
				else
					g_fileIDlimits.CStreaming__ms_aInfoForModelExtension[curID].exUsPrev = curID - 1;

				if (curID == lastID)
					g_fileIDlimits.CStreaming__ms_aInfoForModelExtension[curID].exUsNext = -1;
				else
					g_fileIDlimits.CStreaming__ms_aInfoForModelExtension[curID].exUsNext = curID + 1;

				curID++;

			} while (curID <= lastID);
		}

		curID = firstID;

		do
		{
			if (curID == firstID)
				g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa[curID].usPrev.raw = -1;
			else
				g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa[curID].usPrev.raw = curID - 1;

			if (curID == lastID)
				g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa[curID].usNext.raw = -1;
			else
				g_fileIDlimits.CStreaming__ms_aInfoForModel.gta_sa[curID].usNext.raw = curID + 1;

			curID++;

		} while (curID <= lastID);		
	}

