	extern "C"
	{
		// Validates ped or vehicle model ID
		bool __cdecl ValidatePedOrVehicleID(int ID)
		{
			if (!PROJECT_USE_DEVELOPMENT_INI && ID > MAX_FILE_ID_UINT16)
			{
				const char* str = "Attempt to define a ped/vehicle ID %d higher than %d (MAX_FILE_ID_UINT16). It's not possible.";

				CGenericLogStorage::SaveFormattedTextLn(str, ID, MAX_FILE_ID_UINT16);
				CGenericLogStorage::SaveWorkBuffer();
				printf_MessageBox(str, ID, MAX_FILE_ID_UINT16);

				return false;
			}

			if (ID >= (int)g_fileIDlimits.CDarkel__CountOfKillableModelIDs)
			{
				const char* str = "Attempt to define a ped/vehicle ID %d exceeding the limit of killable model IDs which is %d\n"
					"Please increase this limit.";				

				CGenericLogStorage::SaveFormattedTextLn(
					str,
					ID,
					g_fileIDlimits.CDarkel__CountOfKillableModelIDs
				);

				CGenericLogStorage::SaveWorkBuffer();

				printf_MessageBox(
					str,
					ID,
					g_fileIDlimits.CDarkel__CountOfKillableModelIDs
				);

				return false;
			}

			return true;
		}
	}

