	void CIplStore__IncludeEntity(int IPLid, CEntity *pEntity)
	{
		CPool_GTASA<CIplFile>* pIPLpool = ACCESS_DEF(g_fileIDlimits.CIplStore__ms_pPool.gta_sa);
		auto pBuildingPool = ACCESS_DEF(g_dynamicLimits.CPools__ms_pBuildingPool.gta_sa);
		auto pDummyPool = ACCESS_DEF(g_dynamicLimits.CPools__ms_pDummyPool.gta_sa);

		CIplFile* pFile = pIPLpool->GetAt(IPLid);

		int32_t entityIndex;

		int type = *(char*)(pEntity + 0x36) & 7;

		if (type == 1)
		{
			entityIndex = ((char *)pEntity - (char *)pBuildingPool->m_pObjects) / 56;

			if (entityIndex < GetExtendedIDfrom16bitBefore(&pFile->m_usBuildingsBegin))
				pFile->m_usBuildingsBegin = entityIndex;

			if (entityIndex > GetExtendedIDfrom16bitBefore(&pFile->m_usBuildingsEnd))
				pFile->m_usBuildingsEnd = entityIndex;
		}
		else if (type == 5)
		{
			entityIndex = ((char *)pEntity - (char *)pDummyPool->m_pObjects) / 56;

			if (entityIndex < GetExtendedIDfrom16bitBefore(&pFile->m_usDummyBegin))
				pFile->m_usDummyBegin = entityIndex;

			if (entityIndex > GetExtendedIDfrom16bitBefore(&pFile->m_usDummyEnd))
				pFile->m_usDummyEnd = entityIndex;
		}
	}


	void CColStore__IncludeModelIndex(int COLid, int32_t modelID)
	{
		CPool_GTASA<CCollisionFile>* pCOLpool = ACCESS_DEF(g_fileIDlimits.CColStore__ms_pColPool.gta_sa);

		CCollisionFile* pFile = pCOLpool->GetAt(COLid);

		if (modelID < GetExtendedIDfrom16bitBefore(&pFile->m_wLowestModelID))
			pFile->m_wLowestModelID = modelID;

		if (modelID > GetExtendedIDfrom16bitBefore(&pFile->m_wHighestModelID))
			pFile->m_wHighestModelID = modelID;
	}

