	class CTimeInfo_patch_4C47E0 : public CTimeInfo
	{
	public:
		CTimeInfo* FindOtherTimeModel(const char *modelName)
		{
			int l = strlen(modelName);

			if (l < 3)
				return 0;

			char theModelName[64];

			if (!memcmp(modelName + l - 3, "_dy", 3))
			{
				memcpy(theModelName, modelName, l - 3);
				memcpy(theModelName + l - 3, "_nt", 4);
			}
			else if (!memcmp(modelName + l - 3, "_nt", 3))
			{
				memcpy(theModelName, modelName, l - 3);
				memcpy(theModelName + l - 3, "_dy", 4);
			}
			else
				return 0;


			FileIDlimit* pIDlimits = &g_fileIDlimits;
			uint32_t hash = CKeyGen::GetUppercaseKey(theModelName);
			int32_t ID = pIDlimits->CModelInfo__ms_modelInfoIDhashStore.GetIDbyHashLinearSearch(hash);
			
			if (ID == UNDEFINED_FILE_ID_INT32)
				return 0;

			CBaseModelInfo_generic* pModelInfo = pIDlimits->CModelInfo__ms_modelInfoPtrs.generic[ID];
			
			// GetTimeInfo
			CTimeInfo* pTimeInfo = (*(CTimeInfo*(__thiscall **)(void *))(*(char**)pModelInfo + 20))(pModelInfo);

			if (!pTimeInfo)
				return 0;

			pTimeInfo->m_wOtherTimeModel = ID;

			return pTimeInfo;
		}
	};

