			maxLimitValue = SCHAR_MAX;
		else
			maxLimitValue = false;

		this->RegisterFileType(FILE_TYPE_SCM, 82, maxLimitValue);			// base ID		26230

		// Loaded
		this->RegisterFileType(FILE_TYPE_LOADED_START, 2, false);	// base ID		26312

		// Requested
		this->RegisterFileType(FILE_TYPE_REQUESTED_START, 2, false);// base ID		26314

		// Max count of file IDs		
