// Shutdown
void FileIDlimit::Shutdown()
{
	MAKE_VAR_GAME_VERSION();
		
	delete[] CStreaming__ms_aInfoForModelExtension;
	delete[] CModelInfo__ms_modelInfoExtensionPtrs;
	delete[] CColStore__ms_aColPoolExtension;

	if (CModelInfo__ms_modelInfoPtrs.bIsAllocated.Get())
		delete CModelInfo__ms_modelInfoPtrs.generic;

	if (CSimpleModelInfo__m_UpdatedUVATable.bIsAllocated.Get())
		delete[] CSimpleModelInfo__m_UpdatedUVATable.ptr;
	
	if (CStreaming__ms_aInfoForModel.bIsAllocated.Get())
		delete CStreaming__ms_aInfoForModel.generic;

	if (CAnimManager__ms_aAnimBlocks.bIsAllocated.Get())
		delete CAnimManager__ms_aAnimBlocks.generic;

	if (CVehicleRecording__StreamingArray.bIsAllocated.Get())
		delete CVehicleRecording__StreamingArray.generic;
	
	if (CStreaming__ms_apAGRBuffers.bIsAllocated.Get())
		delete CStreaming__ms_apAGRBuffers.generic;

	if (CTheScripts__StreamedScripts.bIsAllocated.Get())
		delete CTheScripts__StreamedScripts.ptr;

	if (CDarkel__RegisteredKills.bIsAllocated.Get())
		delete[] CDarkel__RegisteredKills.ptr;
}

